interface User {
  name: string;
  email: string;
}

interface UserState {
  user: User | null;
}

const initialState: UserState = {
  user: null,
};

// Reducer: guarda o estado que desejamos compartilhar na aplicação e manipula/altera o estado

interface UserAction {
  type: string;
  payload?: User;
}
//Todo Reducer precisa retornar o nosso estado atualizado
export function userReducer(state = initialState, action: UserAction) {
  if (action.type === "user/login") {
    return {
      user: {
        nome: "Luiz Marcelo",
        email: "lluismarcello333@gmail.com",
      },
    };
  }
}
