import { Products } from "../../data/products";

interface CartState {
  cart: Products[];
}

const initialState: CartState = {
  cart: [],
};

interface CartAction {
  type: string;
  payload: Products;
}

export function cartReducer(state = initialState, action: CartAction) {
  switch (action.type) {
    case "cart/add-product":
      return {
        ...state,
        cart: [...state.cart, action.payload],
      };
  }

  return state;
}
