import { combineReducers } from "redux";
import { cartReducer } from './Cart/cart-reducer';
import { userSlice } from "./User/user-slice";


export const rootReducer = combineReducers({
    userReducer: userSlice.reducer, // Utilizando a Biblioteca Redux, devemos colocar o reducer criado dentro do rootReducer utilizando "userReducer", 
    cartReducer: userSlice.reducer,                    // que foi criado nessaa importação import { userReducer } from "./User/reducer";
});

export type RootReducer = ReturnType<typeof rootReducer>;
